import React, { useEffect, useState } from "react";
import axios from "axios";
import { api_key, base_url } from "./../utils";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 10;
  const totalPages = Math.ceil(products.length / itemsPerPage);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${base_url}/list`, {
        headers: {
          api_key: `${api_key}`,
        },
      });
      // Ensure response contains 'products' and it's an array

      setProducts(response.data.messages);
    } catch (error) {
      console.error("Error fetching products", error);
    }
  };

  // Get the displayed products for the current page
  const displayedProducts = products.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Handle going to the previous page
  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Handle going to the next page
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className="w-full flex flex-col ">
      <table>
        <thead>
          <tr className="flex justify-around items-stretch text-white">
            <th>Product ID</th>
            <th>Product Name</th>
            <th>Original Price</th>
            <th>Sale Price</th>
            <th>Product Type</th>
            <th>Description</th>
            <th>Date and Time</th>
          </tr>
        </thead>
        <tbody>
          {displayedProducts.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_id}</td>
              <td>{product.product_name}</td>
              <td>{product.original_price}</td>
              <td>{product.sale_price}</td>
              <td>{product.product_type}</td>
              <td>{product.description}</td>
              <td>{product.date}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination-controls">
        {/* Disable Previous button on the first page */}
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous
        </button>

        <span>
          Page {currentPage} of {totalPages}
        </span>

        {/* Disable Next button on the last page */}
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

export default ProductList;
