import React, { useEffect, useState } from "react";
import axios from "axios";
import { base_url } from "./../utils";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortField, setSortField] = useState("");
  const [sortDirection, setSortDirection] = useState("asc");

  const itemsPerPage = 10;

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${base_url}/list`, {
        headers: {
          api_key: `${api_key}`,
        },
      });
      setProducts(response.data.products);
    } catch (error) {
      console.error("Error fetching products", error);
    }
  };

  const handleSort = (field) => {
    const direction =
      sortField === field && sortDirection === "asc" ? "desc" : "asc";
    setSortField(field);
    setSortDirection(direction);
    const sortedProducts = [...products].sort((a, b) => {
      if (direction === "asc") return a[field] > b[field] ? 1 : -1;
      return a[field] < b[field] ? 1 : -1;
    });
    setProducts(sortedProducts);
  };

  const filteredProducts = products
    .filter((product) => {
      return (
        product.product_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.product_type.toString().includes(searchTerm)
      );
    })
    .slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div>
      <input
        type="text"
        placeholder="Search by Product Name or Type"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort("product_id")}>Product ID</th>
            <th onClick={() => handleSort("product_name")}>Product Name</th>
            <th onClick={() => handleSort("original_price")}>Original Price</th>
            <th onClick={() => handleSort("sale_price")}>Sale Price</th>
            <th onClick={() => handleSort("product_type")}>Product Type</th>
            <th onClick={() => handleSort("description")}>Description</th>
            <th onClick={() => handleSort("date")}>Date and Time</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_id}</td>
              <td>{product.product_name}</td>
              <td>{product.original_price}</td>
              <td>{product.sale_price}</td>
              <td>{product.product_type}</td>
              <td>{product.description}</td>
              <td>{product.date}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        {Array.from({ length: Math.ceil(products.length / itemsPerPage) }).map(
          (item, index) => (
            <button key={index} onClick={() => setCurrentPage(index + 1)}>
              {index + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default ProductList;
