import React, { useEffect, useState } from "react";
import axios from "axios";
import { api_key, base_url } from "./../utils";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 10;
  const totalPages = Math.ceil(products?.length / itemsPerPage);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${base_url}/list`, {
        headers: {
          api_key: `${api_key}`,
        },
      });
      setProducts(response.data.message);
    } catch (error) {
      console.error("Error fetching products", error);
    }
  };

  const displayedProducts = products.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div className="w-full flex flex-col ">
      <table>
        <thead>
          <tr className="flex justify-around items-stretch text-white">
            <th>Product ID</th>
            <th>Product Name</th>
            <th>Original Price</th>
            <th>Sale Price</th>
            <th>Product Type</th>
            <th>Description</th>
            <th>Date and Time</th>
          </tr>
        </thead>
        <tbody className="flex flex-col justify-around items-stretch text-white">
          {displayedProducts.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_id}</td>
              <td>{product.product_name}</td>
              <td>{product.original_price}</td>
              <td>{product.sale_price}</td>
              <td>{product.product_type}</td>
              <td>{product.description}</td>
              <td>{product.date}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        {Array.from({ length: Math.ceil(products.length / itemsPerPage) }).map(
          (item, index) => (
            <button
              key={index}
              className="bg-blue-500"
              onClick={() => setCurrentPage(index + 1)}
            >
              {index + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default ProductList;
